import React, {Component} from 'react';

class Card extends Component{

  constructor(props){
    super(props)
    this.state = {
      pokemon : null
    }
  }

  
  async componentDidMount(){
    const response = await fetch(`http://localhost:4242/pokemons/${this.props.num}`)
    const data = await response.json()
    this.setState({pokemon : data[0]['nom']})
    //const reset = data[0]['nom'];
  }

  render(){
    return(
      <div>

       
         <span style={{color : 'red'}}> JSON : {this.state.pokemon} </span>
    

      </div>
    )
      
  }


}

export default Card;
