import React from 'react'



import 'bootstrap/dist/css/bootstrap.min.css'
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';



class PokemonDetail extends React.Component{

  constructor(props){
    super(props);

    this.state = {
        id : this.props.match.params.id,
    }
}




async componentDidMount(){
  const response = await fetch(`http://localhost:4242/pokemons/${this.state.id}`)
  const data = await response.json()
  this.setState({pokemon : data})
  console.log(data[0])
}



  render(){
    const p = `https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${this.state.id}.png`
    return (
  
        <div className="row">

           {this.state.id}    
           <img src={p} />

           <div className="row">
            {this.state.pokemon.map(pokemon => (
           
            <CardPokemon key={pokemon.id} name={pokemon.nom} url={"https://assets.pokemon.com/assets/cms2/img/pokedex/detail/"+pokemon.numero+".png"} num={pokemon.numero}/>
             
            ))}
          </div>
         

        </div>
      
    );
  }






  }
export default PokemonDetail;
