import React from 'react'



import 'bootstrap/dist/css/bootstrap.min.css'
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';



class PokemonDetail extends React.Component{

  constructor(props){
    super(props);

    this.state = {
        id : this.props.match.params.id,
    }
}




async componentDidMount(){
  const response = await fetch(`http://localhost:4242/pokemons/${this.state.id}`)
  const data = await response.json()

  this.setState({nom : data[0].nom})
  this.setState({couleur : data[0].couleur})
  this.setState({espece : data[0].espece})
  this.setState({type1 : data[0].type1})
  this.setState({type2 : data[0].type2})
  this.setState({poids : data[0].poids})

  
}



  render(){
    const p = `https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${this.state.id}.png`
    return (
  
        <div className="row">
           {this.state.nom}  
           {this.state.id}
           
           <img src={p} />
         

        </div>
      
    );
  }






  }
export default PokemonDetail;
