  }

  render(){
    return(
      <div>
      {this.state.pokemon ? (
        <div className="row">
          {this.state.pokemon.map(pokemon => (
            <TodoItem />
          ))}
        </div>
      ) : (
        <div>ATTEND</div>
      )}
    </div>
    );
  }






